import React, {Component} from "react";
import "./counter.css";
import {getIncrementCounter, getDecrementCounter, changeValueCounter} from "actions";
import {connect} from "react-redux";

class Counter extends Component {
    render() {
        const {count, maxQuantity, getIncrementCounter, getDecrementCounter, changeValueCounter} = this.props;
        return (
            <div className="level wrapper">
                <div className="level-item">
                    <button className="button btn btnCounter" onClick={() => getIncrementCounter(maxQuantity)}>
                <span className="icon">
                  <i className="fa fa-plus"/>
                </span>
                    </button>
                </div>
                <div className="lead quantity">
                    <input type="text" value={count.value}
                           onChange={(value) => changeValueCounter(value)}/>
                </div>
                <div className="level-item">
                    <button className="button btn btnCounter" onClick={() => getDecrementCounter()}>
                <span className="icon">
                  <i className="fa fa-minus"/>
                </span>
                    </button>
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    count: state.count
});

const mapDispatchToProps = {
    getIncrementCounter,
    getDecrementCounter,
    changeValueCounter
};

export default connect(mapStateToProps, mapDispatchToProps)(Counter);
