import R from 'ramda'

const getCategoryById = (state, id) => R.prop(id, state.categories);

export const getCategories = state => {
    return R.map(id => getCategoryById(state, id), state.phonesPage.ids);
};

export const getItems = state => {
    return state.items
};

export const getTotalBasketCount = state => {
    if (state.basket.length > 0) {
        let countBasket = 0;
        state.basket.map(items => countBasket += items.quantity);
        return countBasket;
    }
    return 0;
};

export const getTotalPrice = state => {
    const {basket} = state;
    if (basket.length > 0) {
        let countBasket = 0;
        let priceBasket = 0;
        state.basket.map(items => countBasket += items.quantity);
        state.basket.map(items => priceBasket += items.cardPrice);
        return countBasket * priceBasket
    }
    return 0;
};
